import React, { useState, useEffect } from "react";
import axios from "axios";
import "../style/index.css"
import Header from "../components/Header";
import { NavLink } from 'react-router-dom'; // Import NavLink for navigation links
import { FaUser, FaEnvelope, FaLock } from 'react-icons/fa'; // Import icons

const Profile = () => {
    const [user, setUser] = useState(null);
    const [editMode, setEditMode] = useState(false);
    const [formData, setFormData] = useState({
        firstname: '',
        lastname: '',
        role: '',
        email: '',
        password: ''
    });

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5000/profile', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setUser(response.data);
                setFormData({
                    firstname: response.data.firstname,
                    lastname: response.data.lastname,
                    role: response.data.role,
                    email: response.data.email,
                    password: ''
                });
            } catch (error) {
                console.error('Erreur lors de la récupération des données utilisateur', error);
            }
        };

        fetchUser();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            await axios.put('http://localhost:5000/profile', formData, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setEditMode(false);
            alert('Les informations de l\'utilisateur ont bien été mises à jour');
        } catch (error) {
            console.error('Erreur lors de la mise à jour du profil', error);
        }
    };

    return (
        <div className="profile-container">
            <div className="left-band">
                <NavLink to="/users" activeClassName="active">
                    <FaUser className="icon" />
                </NavLink>
                <NavLink to="/messages" activeClassName="active">
                    <FaEnvelope className="icon" />
                </NavLink>
                {/* Add other icons as needed */}
            </div>
            <div className="profile-info">
                <Header />
                <h2>Profil de l'utilisateur</h2>
                {user && (
                    <div>
                        {editMode ? (
                            <form onSubmit={handleSubmit}>
                                {/* Form inputs */}
                            </form>
                        ) : (
                            <div>
                                {/* Display profile information */}
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Profile;
