import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "../components/Header";

const Profile = () => {
	const [user, setUser] = useState(null);
	const [editMode, setEditMode] = useState(false);
	const [formData, setFormData] = useState({
		firstname: '',
		lastname: '',
		role: '',
		email: '',
		password: ''
	});

	useEffect(() => {
		const fetchUSer = async () => {
			try {
				const token = localStorage.getItem('token');
				const response = await axios.get('http://localhost:5000/profile', {
					headers: { Authorization: `Bearer ${token}` }
				});
				setUser(response.data);
				setFormData({
					firstname: response.data.firstname,
					lastname: response.data.lastname,
					role: response.data.role,
					email: response.data.email,
					password: ''
				});
			} catch (error) {
				console.error('Erreur lors de la récupération des données utilisateur', error);
			}
		};

		fetchUSer();
	}, []);

	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setFormData({ ...formData, [name]: value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const token = localStorage.getItem('token');
			await axios.put('http://localhost:5000/profile', formData, {
				headers: { Authorization: `Bearer ${token}` }
			});
			setEditMode(false);
			alert('Les informations de l\'utilisateur ont bien été mises à jour');
		} catch (error) {
			console.error('Erreur lors de la mise à jour du profil', error);
		}
	};

	return (
		<div>
			<Header />
			<h2>Profil de l'utilisateur</h2>
			{user && (
				<div>
					{editMode ? (
						<form onSubmit={handleSubmit}>
							<label>Prénom:</label>
							<input
								type="text"
								name="firstname"
								value={formData.firstname}
								onChange={handleInputChange}
							/>
							<label>Nom:</label>
							<input
								type="text"
								name="lastname"
								value={formData.lastname}
								onChange={handleInputChange}
							/>
							<label>Rôle:</label>
							<input
								type="text"
								name="role"
								value={formData.role}
								onChange={handleInputChange}
							/>
							<label>Email:</label>
							<input
								type="email"
								name="email"
								value={formData.email}
								onChange={handleInputChange}
							/>
							<label>Mot de passe:</label>
							<input
								type="password"
								name="password"
								value={formData.password}
								onChange={handleInputChange}
							/>
							<button type="submit">Mettre à jour</button>
							<button onClick={() => setEditMode(false)}>Annuler</button>
						</form>
					) : (
						<div>
							<p><strong>Prénom:</strong> {user.firstname}</p>
							<p><strong>Nom:</strong> {user.lastname}</p>
							<p><strong>Rôle:</strong> {user.role}</p>
							<p><strong>Email:</strong> {user.email}</p>
							<button onClick={() => setEditMode(true)}>Modifier le profil</button>
						</div>
					)}
				</div>
			)}
		</div>
	);
};

export default Profile;
