import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UpdateEmployee from './UpdateEmployee';
import AddEmployee from './AddEmployee';
import Header from './Header';
import Index from './Index';
import '../style/User.css';

const UsersList = () => {
	const [users, setUsers] = useState([]);
	const [selectedUser, setSelectedUser] = useState(null);
	const [showAddForm, setShowAddForm] = useState(false);

	const fetchUsers = () => {
		const token = localStorage.getItem('token');
		if (!token) {
			console.error('Pas de token trouvé, redirection vers l\'authentification...');
			navigate('/login');
			return;
		}
		axios.get('http://localhost:5000/users', {
			headers: { Authorization: `Bearer ${token}` }
		})
			.then(response => {
				setUsers(response.data);
			})
			.catch(error => {
				console.error('Il y a une erreur avec les utilisateurs !', error);
			});
	};

	useEffect(() => {
		fetchUsers();
	}, []);

	const handleDelete = (id) => {
		const token = localStorage.getItem('token');
		axios.delete(`http://localhost:5000/users/${id}`, {
			headers: { Authorization: `Bearer ${token}` }
		})
			.then(response => {
				console.log(response.data.message);
				fetchUsers();
			})
			.catch(error => {
				console.error('Erreur lors de la suppression de l\'utilisateur', error);
			});
	};

	return (
		<div>
			<Header />
			<div className="container">
				<Index />
				<div className="main-content">
					<h1>Répertoire des utilisateurs</h1>
					<ul className="user-list">
						{users.map(emp => (
							<li key={emp.id}>
								<div>
									{selectedUser && selectedUser.id === emp.id ? (
										<UpdateEmployee user={selectedUser} onUpdate={() => {
											fetchUsers();
											selectedUser(null);
										}} />
									) : (
										<>
											<div>{emp.firstname} {emp.lastname} - {emp.role}</div>
											<div>{emp.email}</div>
											<div>
												<button onClick={() => handleDelete(emp.id)} style={{ marginRight: '10px' }}>Supprimer cet utilisateur</button>
												<button onClick={() => setSelectedUser(emp)}>Modifier</button>
											</div>
										</>
									)}
								</div>
							</li>
						))}
					</ul>
					<div className='add-user-container'>
						{showAddForm ? (
							<AddEmployee onAdd={() => {
								fetchUsers();
								setShowAddForm(false);
							}} />
						) : (
							<button onClick={() => setShowAddForm(true)}>Ajouter un utilisateur</button>
						)}
					</div>
				</div>
			</div>
		</div>
	);
};

export default UsersList;
